mapper.select:
SELECT ap.*, ( SELECT COUNT(*) FROM t_package_app WHERE app_package_id = ap.id ) AS app_count, ( SELECT IFNULL(SUM(r.size),0) FROM t_resources r LEFT JOIN t_app a ON a.resources_id = r.id LEFT JOIN t_package_app pa ON pa.app_id = a.id WHERE pa.app_package_id = ap.id ) app_size FROM t_app_package ap
ORDER BY app_size ${sortDir}
ORDER BY app_count ${sortDir}
ORDER BY ap.${sortName} ${sortDir}

SELECT a.* FROM t_app a
LEFT JOIN t_package_app pa ON pa.app_id = a.id
WHERE pa.type = #{type} and pa.app_package_id = #{id}

    ORDER BY a.${sortName} ${sortDir}

SELECT a.* FROM t_app a
LEFT JOIN t_package_app pa ON pa.app_id = a.id
WHERE pa.app_package_id = #{id}

    and pa.type = #{type}
