mapper.select:
SELECT p.*, img_r.size as img_size, img_r.name as img_name, img_r.md5 as img_md5, img_r.create_time as img_time, app_r.size as app_size, app_r.name as app_name, app_r.md5 as app_md5, app_r.create_time as app_time, u.name AS ow FROM t_push p LEFT JOIN t_resources img_r ON img_r.id = p.img_resources_id LEFT JOIN t_resources app_r ON app_r.id = p.app_resources_id LEFT JOIN t_user u ON u.id = p.user_id
ORDER BY u.name ${sortDir}
ORDER BY p.${sortName} ${sortDir}
