mapper.select:
SELECT rp.*, ( SELECT COUNT(*) FROM t_package_app WHERE app_package_id = rp.id ) AS app_count, ( SELECT IFNULL(SUM(r.size),0) FROM t_resources r LEFT JOIN t_app a ON a.resources_id = r.id LEFT JOIN t_package_app pa ON pa.app_id = a.id WHERE pa.app_package_id = rp.id ) app_size FROM t_require_package rp
ORDER BY app_size ${sortDir}
ORDER BY app_count ${sortDir}
ORDER BY rp.${sortName} ${sortDir}

SELECT ar.* FROM t_app_require ar
LEFT JOIN t_package_app pa ON pa.app_id = ar.id
WHERE pa.type = #{type} and pa.app_package_id = #{id}

    ORDER BY ar.${sortName} ${sortDir}

SELECT ar.* FROM t_app_require ar
LEFT JOIN t_package_app pa ON pa.app_id = ar.id
WHERE pa.app_package_id = #{id}

    and pa.type = #{type}
