mapper.select:
SELECT
rc.*,
r.name AS resources_name, r.md5, r.size
FROM
t_root_config rc
LEFT JOIN t_model_ref mr ON mr.ref_id = rc.id
LEFT JOIN t_resources r ON r.id = rc.resources_id
WHERE
   mr.type = 2

    AND mr.brand_id = #{brand_id}


    AND mr.model_id = #{model_id}


    AND mr.version_id = #{version_id}


    AND mr.base_version_id = #{base_version_id}
